    return axios
      .post('/api/login', {
        email,
        password
      })
      .then(res => {
        commit('SET_USER', res.data);
      })
      .catch(error => {
        if (error.response.status === 401) {
          throw new Error('Bad credentials');
        }
      });
  },

  register({ commit }, { email, password }) {
    return axios
      .post('/api/register', {
        email,
        password
      })
      .then(res => {
